import React, { useState } from "react";
import { Table, Input, Pagination } from "antd";
import axios from "axios";
import { userColumns } from "./columns";
import { useTableSearch } from "./useTableSearch";
import "antd/dist/antd.css";
import "./styling.css";

const { Search } = Input;

const fetchUsers = async () => {
  const { data } = await axios.get(
    "https://vast-shore-74260.herokuapp.com/banks?city=MUMBAI"
  );
  return { data };
};

export default function App() {
  const [searchVal, setSearchVal] = useState(null);

  const { filteredData, loading } = useTableSearch({
    searchVal,
    retrieve: fetchUsers,
  });
  function onChange2(pageNumber) {
    console.log("Page: ", pageNumber);
  }

  const rowSelection = {
    onChange: (selectedRowKeys, selectedRows) => {
      localStorage.setItem("selection", JSON.stringify(selectedRows));
      console.log(localStorage);
      console.log(
        `selectedRowKeys: ${selectedRowKeys}`,
        "selectedRows: ",
        selectedRows
      );
    },
    getCheckboxProps: (record) => ({
      disabled: record.ifsc === "Disabled User", // Column configuration not to be checked
      ifsc: record.ifsc,
      className: "checkbox-red",
    }),
  };
  // var storage = localStorage.getItem("selection");
  // console.log(storage);
  // var dataSource2 = this.storage;
  return (
    <>
      <div className="container">
        <div className="container2">
          <Search
            onChange={(e) => setSearchVal(e.target.value)}
            placeholder="Search"
          />
        </div>
        <br /> <br />
        <Table
          rowSelection={rowSelection}
          rowKey="ifsc"
          dataSource={filteredData}
          columns={userColumns}
          loading={loading}
          pagination={
            <Pagination
              size="small"
              showSizeChanger
              showQuickJumper
              defaultCurrent={2}
              total={500}
              onChange={onChange2}
            />
          }
        />
        <h1>Favourites</h1>
        <Table
          rowKey="ifsc"
          dataSource={dataSource2}
          columns={userColumns}
          loading={loading}
        />
      </div>
    </>
  );
}
